import React from 'react';
import BasketItem from "./BasketItem";
import {inject, observer} from 'mobx-react';

interface IBasketItemList {
    item: object;
    total: number;
    onTake: object;
    name: string;
    price: number;
    count: number;
}

function BasketItemList({item, total, onTake}: IBasketItemList) {
    const itemList = item.map(item => (
        <BasketItemList
            name={item.name}
            price={item.price}
            count={item.count}
            key={item.name}
            onTake={onTake}
        />
    ));
    return(
        <div>
            {itemList}
            <hr />
            <p>
                <b>총합: </b> {total}원
            </p>
        </div>
    );

}

export default inject(({market}) => ({
    items: market.selectedItem,
    total: market.total,
    onTake: market.take,
}))(observer(BasketItemList));
