import React, {useState} from "react";

function Counter() {
    const [Count, Counter] = useState(0); //원래방식
    // let Count = 0;

    const On = () =>{
        // Count++;
        Counter(Count+1);
    }

    const Off = () => {
        // Count--;
        Counter(Count-1);
    }

    return(
        <div>
            <div className="counter">
                {Count}
            </div>
            <br/>
            <button onClick={On}>+1</button>
            <button onClick={Off}>-1</button>
        </div>
    );
}

export default Counter;
